import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Calendar, MapPin, Users, Award, Music, Camera, Utensils } from 'lucide-react';
import CountdownTimer from '../components/CountdownTimer';
import ArtistShowcase from '../components/ArtistShowcase';
import FestivalHighlights from '../components/FestivalHighlights';
import NotionCupPreview from '../components/NotionCupPreview';
import PhaseOneBanner from '../components/PhaseOneBanner';
import './Home.css';

const Home: React.FC = () => {
  const [stars, setStars] = useState<Array<{ id: number; x: number; y: number; size: number; delay: number }>>([]);

  useEffect(() => {
    // Generate random stars for background
    const generateStars = () => {
      const newStars = Array.from({ length: 50 }, (_, i) => ({
        id: i,
        x: Math.random() * 100,
        y: Math.random() * 100,
        size: Math.random() * 3 + 1,
        delay: Math.random() * 3,
      }));
      setStars(newStars);
    };

    generateStars();
  }, []);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut",
      },
    },
  };

  return (
    <div className="home">
      {/* Starfield Background */}
      <div className="starfield">
        {stars.map((star) => (
          <div
            key={star.id}
            className="star"
            style={{
              left: `${star.x}%`,
              top: `${star.y}%`,
              width: `${star.size}px`,
              height: `${star.size}px`,
              animationDelay: `${star.delay}s`,
            }}
          />
        ))}
      </div>

      {/* Hero Section */}
      <section className="hero">
        <div className="container">
          <motion.div
            className="hero-content"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <motion.div className="hero-flags" variants={itemVariants}>
              <div className="flag flag-uae">🇦🇪</div>
              <div className="flag flag-spain">🇪🇸</div>
              <div className="flag flag-thailand">🇹🇭</div>
              <div className="flag flag-uk">🇬🇧</div>
            </motion.div>

            <motion.div className="hero-text" variants={itemVariants}>
              <h1 className="hero-title">
                <span className="title-latin">LATIN</span>
                <div className="title-notion-container">
                  <div className="disco-element disco-left">🎵</div>
                  <span className="title-notion">
                    NoTioN
                    <span className="flame-icon">🔥</span>
                  </span>
                  <div className="disco-element disco-right">🎵</div>
                </div>
                <span className="title-bachata">Bachata Festival</span>
                <span className="title-dubai">DUBAI</span>
              </h1>
              
              <div className="edition-badge">
                <span>3RD EDITION</span>
              </div>
              
              <div className="event-dates">
                <Calendar className="date-icon" />
                <span>NOV 19TH - 24TH 2025</span>
              </div>
            </motion.div>

            <motion.div className="hero-countdown" variants={itemVariants}>
              <CountdownTimer targetDate="2025-11-19T00:00:00" />
            </motion.div>

            <motion.div className="hero-cta" variants={itemVariants}>
              <Link to="/tickets" className="btn btn-primary btn-large">
                Buy Tickets Now
              </Link>
              <Link to="/festival" className="btn btn-secondary btn-large">
                Learn More
              </Link>
            </motion.div>
          </motion.div>
        </div>
      </section>

      {/* Phase 1 Banner */}
      <PhaseOneBanner />

      {/* Festival Highlights */}
      <FestivalHighlights />

      {/* Artists Showcase */}
      <ArtistShowcase />

      {/* Notion Cup Preview */}
      <NotionCupPreview />

      {/* Quick Info Section */}
      <section className="quick-info">
        <div className="container">
          <motion.div
            className="info-grid"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <div className="info-card">
              <div className="info-icon">
                <MapPin size={32} />
              </div>
              <h3>Location</h3>
              <p>Dubai, UAE</p>
            </div>
            
            <div className="info-card">
              <div className="info-icon">
                <Users size={32} />
              </div>
              <h3>Participants</h3>
              <p>500+ Dancers</p>
            </div>
            
            <div className="info-card">
              <div className="info-icon">
                <Award size={32} />
              </div>
              <h3>International</h3>
              <p>World-Class Artists</p>
            </div>
            
            <div className="info-card">
              <div className="info-icon">
                <Music size={32} />
              </div>
              <h3>Experience</h3>
              <p>6 Days of Bachata</p>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Festival Features */}
      <section className="festival-features">
        <div className="container">
          <motion.div
            className="features-content"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2 className="section-title">Why Choose Latin Notion?</h2>
            <p className="section-subtitle">
              Experience the ultimate bachata festival with world-class artists, 
              immersive workshops, and unforgettable celebrations.
            </p>
            
            <div className="features-grid">
              <div className="feature-item">
                <div className="feature-icon">
                  <Users size={40} />
                </div>
                <h3>World-Class Artists</h3>
                <p>Learn from the best international bachata instructors and performers from around the globe.</p>
              </div>
              
              <div className="feature-item">
                <div className="feature-icon">
                  <Music size={40} />
                </div>
                <h3>Immersive Workshops</h3>
                <p>Daily deep-dives on musicality, partner connection, styling, and performance techniques.</p>
              </div>
              
              <div className="feature-item">
                <div className="feature-icon">
                  <Camera size={40} />
                </div>
                <h3>Iconic Backdrops</h3>
                <p>Dance against dramatic desert sunsets, grand ballrooms, and stunning rooftop terraces.</p>
              </div>
              
              <div className="feature-item">
                <div className="feature-icon">
                  <Utensils size={40} />
                </div>
                <h3>Cultural Fusion</h3>
                <p>Experience Latin-Middle Eastern culinary pop-ups and artisan markets.</p>
              </div>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default Home;
