import React from 'react';
import { motion } from 'framer-motion';
import { MapPin, Star, Award } from 'lucide-react';
import './Artists.css';
import NotionCupPreview from '../components/NotionCupPreview';



const Artists: React.FC = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: { staggerChildren: 0.2 },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.6, ease: "easeOut" },
    },
  };

  return (
    <>
      {/* âœ… NotionCupPreview at the top */}
      <NotionCupPreview />

      {/* Line Up Section */}
      <section className="lineup-section">
        {/* Centered Image */}
        <div className="lineup-hero-image">
          <img 
            src="/placeholder-lineup-hero.jpg" 
            alt="Line Up Hero" 
            className="lineup-hero-img"
          />
        </div>

        {/* Bachata Line Up */}
        <div className="lineup-content">
          <h2 className="lineup-title">
            BACHATA LINE UP 2025<br />
            <span className="lineup-subtitle">LATIN NOTION BACHATA FESTIVAL, DUBAI</span>
          </h2>

          <div className="lineup-grid bachata-grid">
            {Array.from({ length: 12 }, (_, index) => (
              <div key={`bachata-${index}`} className="lineup-item">
                <img 
                  src={`/placeholder-bachata-${index + 1}.jpg`} 
                  alt={`Bachata Artist ${index + 1}`}
                  className="lineup-image"
                />
              </div>
            ))}
          </div>

          <h2 className="lineup-title">
            DJ LINE UP 2025<br />
            <span className="lineup-subtitle">LATIN NOTION BACHATA FESTIVAL, DUBAI</span>
          </h2>

          <div className="lineup-grid dj-grid">
            {Array.from({ length: 4 }, (_, index) => (
              <div key={`dj-${index}`} className="lineup-item">
                <img 
                  src={`/placeholder-dj-${index + 1}.jpg`} 
                  alt={`DJ ${index + 1}`}
                  className="lineup-image"
                />
              </div>
            ))}
          </div>
        </div>
      </section>

      <div className="artists-page">
        <div className="container">
          {/* Header */}
          <motion.section
            className="artists-header"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
          >
            <h1 className="page-title">Our Featured Artists</h1>
            <p className="page-subtitle">
              Meet the world-class bachata artists who will be teaching and performing at the festival
            </p>
          </motion.section>

          {/* Artists Grid */}
          <motion.section
            className="artists-grid"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            {artists.map((artist) => (
              <motion.div
                key={artist.id}
                className="artist-card"
                variants={itemVariants}
                whileHover={{ y: -10 }}
                transition={{ duration: 0.3 }}
              >
                <div className="artist-image-container">
                  <div className="artist-image">
                    <img
                      src={artist.image}
                      alt={artist.name}
                      onError={(e) => {
                        const target = e.target as HTMLImageElement;
                        target.src = `https://via.placeholder.com/300x400/6B46C1/FFFFFF?text=${encodeURIComponent(
                          artist.name
                        )}`;
                      }}
                    />
                  </div>
                  {artist.featured && (
                    <div className="featured-badge">
                      <Star size={16} />
                      <span>Featured Artist</span>
                    </div>
                  )}
                </div>

                <div className="artist-content">
                  <div className="artist-header">
                    <h3 className="artist-name">{artist.name}</h3>
                    <div className="artist-origin">
                      <MapPin size={16} />
                      <span>
                        {artist.origin} {artist.flag}
                      </span>
                    </div>
                    <p className="artist-style">{artist.style}</p>
                  </div>

                  <p className="artist-description">{artist.description}</p>

                  <div className="artist-achievements">
                    <h4>Achievements</h4>
                    <ul>
                      {artist.achievements.map((achievement, index) => (
                        <li key={index}>
                          <Award size={14} />
                          <span>{achievement}</span>
                        </li>
                      ))}
                    </ul>
                  </div>

                  {artist.socialMedia && (
                    <div className="artist-social">
                      <h4>Follow Them</h4>
                      <div className="social-links">
                        {artist.socialMedia.instagram && (
                          <a
                            href={`https://instagram.com/${artist.socialMedia.instagram.replace('@', '')}`}
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            Instagram
                          </a>
                        )}
                        {artist.socialMedia.facebook && (
                          <a
                            href={`https://facebook.com/${artist.socialMedia.facebook}`}
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            Facebook
                          </a>
                        )}
                        {artist.socialMedia.youtube && (
                          <a
                            href={`https://youtube.com/c/${artist.socialMedia.youtube.replace(' ', '')}`}
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            YouTube
                          </a>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </motion.div>
            ))}
          </motion.section>

          {/* Call to Action */}
          <motion.section
            className="artists-cta"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <div className="cta-content">
              <h2>Ready to Learn from the Best?</h2>
              <p>
                Join us at the Latin Notion Bachata Festival and learn directly
                from these world-class artists.
              </p>
              <div className="cta-buttons">
                <a href="/tickets" className="btn btn-primary">
                  Get Your Tickets
                </a>
                <a href="/festival" className="btn btn-secondary">
                  Learn More
                </a>
              </div>
            </div>
          </motion.section>
        </div>
      </div>
    </>
  );
};

export default Artists;
