import React from 'react';
import { motion } from 'framer-motion';
import { Heart, Users, Globe, Award, Calendar, MapPin } from 'lucide-react';
import './About.css';

const About: React.FC = () => {
  const values = [
    {
      icon: <Heart size={40} />,
      title: 'Passion for Dance',
      description: 'We believe in the transformative power of bachata to bring people together and create lasting connections.'
    },
    {
      icon: <Users size={40} />,
      title: 'Community First',
      description: 'Building a global community of dancers who support, inspire, and learn from each other.'
    },
    {
      icon: <Globe size={40} />,
      title: 'Cultural Exchange',
      description: 'Celebrating the rich diversity of Latin culture while embracing the beauty of Middle Eastern traditions.'
    },
    {
      icon: <Award size={40} />,
      title: 'Excellence',
      description: 'Committed to providing world-class experiences with the best artists, venues, and organization.'
    }
  ];

  const team = [
    {
      name: 'Festival Director',
      role: 'Event Management',
      description: 'Leading the vision and execution of the Latin Notion Bachata Festival.'
    },
    {
      name: 'Artistic Director',
      role: 'Artist Relations',
      description: 'Curating world-class artists and ensuring exceptional workshop experiences.'
    },
    {
      name: 'Community Manager',
      role: 'Participant Experience',
      description: 'Creating memorable experiences and fostering connections among festival attendees.'
    }
  ];

  return (
    <div className="about-page">
      <div className="container">
        {/* Hero Section */}
        <motion.section
          className="about-hero"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <h1 className="page-title">About Latin Notion</h1>
          <p className="page-subtitle">
            Bringing the world's best bachata artists to Dubai for an unforgettable festival experience
          </p>
        </motion.section>

        {/* Story Section */}
        <section className="our-story">
          <motion.div
            className="story-content"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <div className="story-text">
              <h2>Our Story</h2>
              <p>
                Latin Notion was born from a passion for bachata and a vision to create a world-class 
                festival experience in the heart of Dubai. What started as a small gathering of dance 
                enthusiasts has evolved into one of the most anticipated bachata festivals in the region.
              </p>
              <p>
                Our 3rd edition represents years of dedication to bringing together the best international 
                artists, creating immersive learning experiences, and fostering a global community of 
                bachata lovers. We believe in the power of dance to transcend cultural boundaries and 
                create lasting connections.
              </p>
              <p>
                From our humble beginnings to becoming a premier destination for bachata enthusiasts 
                worldwide, Latin Notion continues to push the boundaries of what a dance festival can be.
              </p>
            </div>
            <div className="story-stats">
              <div className="stat-item">
                <Calendar size={32} />
                <div>
                  <h3>3rd Edition</h3>
                  <p>Years of Excellence</p>
                </div>
              </div>
              <div className="stat-item">
                <Users size={32} />
                <div>
                  <h3>500+</h3>
                  <p>Annual Participants</p>
                </div>
              </div>
              <div className="stat-item">
                <Globe size={32} />
                <div>
                  <h3>20+</h3>
                  <p>Countries Represented</p>
                </div>
              </div>
              <div className="stat-item">
                <MapPin size={32} />
                <div>
                  <h3>Dubai</h3>
                  <p>Iconic Location</p>
                </div>
              </div>
            </div>
          </motion.div>
        </section>

        {/* Values Section */}
        <section className="our-values">
          <motion.div
            className="values-header"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2 className="section-title">Our Values</h2>
            <p className="section-subtitle">
              The principles that guide everything we do at Latin Notion
            </p>
          </motion.div>

          <motion.div
            className="values-grid"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            {values.map((value, index) => (
              <motion.div
                key={index}
                className="value-card"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                whileHover={{ y: -5 }}
              >
                <div className="value-icon">
                  {value.icon}
                </div>
                <h3 className="value-title">{value.title}</h3>
                <p className="value-description">{value.description}</p>
              </motion.div>
            ))}
          </motion.div>
        </section>

        {/* Team Section */}
        <section className="our-team">
          <motion.div
            className="team-header"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2 className="section-title">Our Team</h2>
            <p className="section-subtitle">
              The passionate individuals behind Latin Notion
            </p>
          </motion.div>

          <motion.div
            className="team-grid"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            {team.map((member, index) => (
              <motion.div
                key={index}
                className="team-card"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                whileHover={{ y: -5 }}
              >
                <h3 className="member-name">{member.name}</h3>
                <p className="member-role">{member.role}</p>
                <p className="member-description">{member.description}</p>
              </motion.div>
            ))}
          </motion.div>
        </section>

        {/* Mission Section */}
        <section className="our-mission">
          <motion.div
            className="mission-content"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2>Our Mission</h2>
            <p>
              To create the world's most immersive and inclusive bachata festival experience, 
              bringing together dancers from all backgrounds and skill levels to learn, 
              connect, and celebrate the beautiful art of bachata in the stunning setting of Dubai.
            </p>
            <div className="mission-highlights">
              <div className="highlight">
                <h4>Inclusive</h4>
                <p>Welcoming dancers of all levels and backgrounds</p>
              </div>
              <div className="highlight">
                <h4>Educational</h4>
                <p>World-class workshops and masterclasses</p>
              </div>
              <div className="highlight">
                <h4>Cultural</h4>
                <p>Celebrating Latin and Middle Eastern fusion</p>
              </div>
            </div>
          </motion.div>
        </section>
      </div>
    </div>
  );
};

export default About;
