import React from 'react';
import { motion } from 'framer-motion';
import { MapPin, Star, Award } from 'lucide-react';
import './Artists.css';

interface Artist {
  id: number;
  name: string;
  origin: string;
  flag: string;
  style: string;
  image: string;
  description: string;
  achievements: string[];
  featured: boolean;
  socialMedia: {
    instagram?: string;
    facebook?: string;
    youtube?: string;
  };
}

const artists: Artist[] = [
  {
    id: 1,
    name: "Cornel & Rithika",
    origin: "India",
    flag: "ðŸ‡®ðŸ‡³",
    style: "CONRI STYLE BACHATA",
    image: "/api/placeholder/300/400",
    description: "Revolutionary bachata style creators from India, bringing fresh perspectives to the dance world with their innovative Conri Style Bachata.",
    achievements: [
      "Creators of Conri Style Bachata",
      "International Bachata Champions",
      "Featured in major dance festivals worldwide",
      "YouTube sensations with millions of views"
    ],
    featured: true,
    socialMedia: {
      instagram: "@cornel_rithika",
      youtube: "Cornel & Rithika Official"
    }
  },
  {
    id: 2,
    name: "Ronald & Alba",
    origin: "Spain",
    flag: "ðŸ‡ªðŸ‡¸",
    style: "BACHATA",
    image: "/api/placeholder/300/400",
    description: "Spanish bachata masters known for their passionate and authentic dance style, bringing the true essence of Dominican bachata to the world.",
    achievements: [
      "Spanish Bachata Champions",
      "World Bachata Masters",
      "International workshop instructors",
      "Featured performers at major festivals"
    ],
    featured: true,
    socialMedia: {
      instagram: "@ronald_alba_bachata",
      facebook: "Ronald & Alba Bachata"
    }
  },
  {
    id: 3,
    name: "Korke & Judith",
    origin: "Spain",
    flag: "ðŸ‡ªðŸ‡¸",
    style: "CREATORS OF BACHATA SENSUAL",
    image: "/api/placeholder/300/400",
    description: "The legendary creators of Bachata Sensual, revolutionizing the dance with their innovative style that has influenced millions of dancers worldwide.",
    achievements: [
      "Creators of Bachata Sensual",
      "World-renowned dance instructors",
      "International Bachata Champions",
      "Authors of bachata instructional materials"
    ],
    featured: true,
    socialMedia: {
      instagram: "@korke_judith",
      youtube: "Korke & Judith Official"
    }
  },
  {
    id: 4,
    name: "Leandro & Jomante",
    origin: "Spain | Lithuania",
    flag: "ðŸ‡ªðŸ‡¸ðŸ‡±ðŸ‡¹",
    style: "BACHATA",
    image: "/api/placeholder/300/400",
    description: "International bachata duo bringing together Spanish passion and Lithuanian precision, creating a unique fusion that captivates audiences worldwide.",
    achievements: [
      "International Bachata Champions",
      "European Bachata Masters",
      "Featured in major dance competitions",
      "Workshop instructors across Europe"
    ],
    featured: true,
    socialMedia: {
      instagram: "@leandro_jomante",
      facebook: "Leandro & Jomante"
    }
  }
];

const Artists: React.FC = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut",
      },
    },
  };

  return (
    <div className="artists-page">
      <div className="container">
        {/* Header */}
        <motion.section
          className="artists-header"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <h1 className="page-title">Our Featured Artists</h1>
          <p className="page-subtitle">
            Meet the world-class bachata artists who will be teaching and performing at the festival
          </p>
        </motion.section>

        {/* Artists Grid */}
        <motion.section
          className="artists-grid"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          {artists.map((artist) => (
            <motion.div
              key={artist.id}
              className="artist-card"
              variants={itemVariants}
              whileHover={{ y: -10 }}
              transition={{ duration: 0.3 }}
            >
              <div className="artist-image-container">
                <div className="artist-image">
                  <img 
                    src={artist.image} 
                    alt={artist.name}
                    onError={(e) => {
                      const target = e.target as HTMLImageElement;
                      target.src = `https://via.placeholder.com/300x400/6B46C1/FFFFFF?text=${encodeURIComponent(artist.name)}`;
                    }}
                  />
                </div>
                {artist.featured && (
                  <div className="featured-badge">
                    <Star size={16} />
                    <span>Featured Artist</span>
                  </div>
                )}
              </div>

              <div className="artist-content">
                <div className="artist-header">
                  <h3 className="artist-name">{artist.name}</h3>
                  <div className="artist-origin">
                    <MapPin size={16} />
                    <span>{artist.origin} {artist.flag}</span>
                  </div>
                  <p className="artist-style">{artist.style}</p>
                </div>

                <p className="artist-description">{artist.description}</p>

                <div className="artist-achievements">
                  <h4>Achievements</h4>
                  <ul>
                    {artist.achievements.map((achievement, index) => (
                      <li key={index}>
                        <Award size={14} />
                        <span>{achievement}</span>
                      </li>
                    ))}
                  </ul>
                </div>

                {artist.socialMedia && (
                  <div className="artist-social">
                    <h4>Follow Them</h4>
                    <div className="social-links">
                      {artist.socialMedia.instagram && (
                        <a href={`https://instagram.com/${artist.socialMedia.instagram.replace('@', '')}`} target="_blank" rel="noopener noreferrer">
                          Instagram
                        </a>
                      )}
                      {artist.socialMedia.facebook && (
                        <a href={`https://facebook.com/${artist.socialMedia.facebook}`} target="_blank" rel="noopener noreferrer">
                          Facebook
                        </a>
                      )}
                      {artist.socialMedia.youtube && (
                        <a href={`https://youtube.com/c/${artist.socialMedia.youtube.replace(' ', '')}`} target="_blank" rel="noopener noreferrer">
                          YouTube
                        </a>
                      )}
                    </div>
                  </div>
                )}
              </div>
            </motion.div>
          ))}
        </motion.section>

        {/* Call to Action */}
        <motion.section
          className="artists-cta"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          <div className="cta-content">
            <h2>Ready to Learn from the Best?</h2>
            <p>Join us at the Latin Notion Bachata Festival and learn directly from these world-class artists.</p>
            <div className="cta-buttons">
              <a href="/tickets" className="btn btn-primary">Get Your Tickets</a>
              <a href="/festival" className="btn btn-secondary">Learn More</a>
            </div>
          </div>
        </motion.section>
      </div>
    </div>
  );
};

export default Artists;
