import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Calendar, MapPin, Users, Award, Music } from 'lucide-react';
import CountdownTimer from '../components/CountdownTimer';
import ArtistShowcase from '../components/ArtistShowcase';
import FestivalHighlights from '../components/FestivalHighlights';
import NotionCupPreview from '../components/NotionCupPreview';
import PhaseOneBanner from '../components/PhaseOneBanner';
import { useAuth } from '../context/AuthContext'; // Import from your context
import './Home.css';

const Home: React.FC = () => {
  const { handleBuyTicketsClick } = useAuth(); // Use context function

  // ... your star generation and other code ...

  return (
    <div className="home">
      {/* ... starfield and hero content ... */}

      <section className="hero">
        <div className="container">
          <motion.div
            className="hero-content"
            initial="hidden"
            animate="visible"
            // ...variants as needed...
          >
            {/* ...hero logo and event dates... */}
          </motion.div>

          <motion.div className="hero-countdown" /*variants*/>
            <CountdownTimer targetDate="2025-11-19T00:00:00" />
          </motion.div>

          <motion.div className="hero-cta" /*variants*/>
            {/* Changed Link to button with click handler */}
            <button className="btn btn-primary btn-large" onClick={handleBuyTicketsClick}>
              Buy Tickets Now
            </button>
            <Link to="/festival" className="btn btn-secondary btn-large">
              Learn More
            </Link>
          </motion.div>
        </div>
      </section>

      {/* Rest of your home page content unchanged */}
      <ArtistShowcase />
      <NotionCupPreview />
      <FestivalHighlights />
      <PhaseOneBanner />

      {/* ... quick info section ... */}
    </div>
  );
};

export default Home;
