import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Menu, X, ChevronDown } from 'lucide-react';
import './Header.css';

const Header: React.FC = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState<string | null>(null);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleDropdown = (dropdown: string) => {
    setIsDropdownOpen(isDropdownOpen === dropdown ? null : dropdown);
  };

  const isActive = (path: string) => {
    return location.pathname === path;
  };

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="container">
        <div className="header-content">
          {/* Logo */}
          <Link to="/" className="logo">
            <span className="logo-text">
              <span className="logo-latin">LATI</span>
              <span className="logo-notion">
                NoTioN
                <span className="flame">🔥</span>
              </span>
            </span>
          </Link>

          {/* Desktop Navigation */}
          <nav className="desktop-nav">
            <Link 
              to="/" 
              className={`nav-link ${isActive('/') ? 'active' : ''}`}
            >
              Home
            </Link>
            
            <div className="dropdown">
              <button 
                className={`nav-link dropdown-toggle ${isActive('/festival') ? 'active' : ''}`}
                onClick={() => toggleDropdown('festival')}
              >
                The Festival
                <ChevronDown size={16} />
              </button>
              {isDropdownOpen === 'festival' && (
                <div className="dropdown-menu">
                  <Link to="/festival" className="dropdown-item">Festival Overview</Link>
                  <Link to="/festival#highlights" className="dropdown-item">Highlights</Link>
                  <Link to="/festival#schedule" className="dropdown-item">Schedule</Link>
                </div>
              )}
            </div>

            <Link 
              to="/artists" 
              className={`nav-link ${isActive('/artists') ? 'active' : ''}`}
            >
              Artists
            </Link>

            <div className="dropdown">
              <button 
                className={`nav-link dropdown-toggle ${isActive('/notion-cup') ? 'active' : ''}`}
                onClick={() => toggleDropdown('notion-cup')}
              >
                Notion Cup
                <ChevronDown size={16} />
              </button>
              {isDropdownOpen === 'notion-cup' && (
                <div className="dropdown-menu">
                  <Link to="/notion-cup" className="dropdown-item">Competition</Link>
                  <Link to="/notion-cup#rules" className="dropdown-item">Rules</Link>
                  <Link to="/notion-cup#prizes" className="dropdown-item">Prizes</Link>
                </div>
              )}
            </div>

            <Link 
              to="/about" 
              className={`nav-link ${isActive('/about') ? 'active' : ''}`}
            >
              About
            </Link>

            <Link 
              to="/contact" 
              className={`nav-link ${isActive('/contact') ? 'active' : ''}`}
            >
              Contact
            </Link>
          </nav>

          {/* CTA Button */}
          <Link to="/tickets" className="btn btn-primary cta-button">
            Buy Tickets
          </Link>

          {/* Mobile Menu Button */}
          <button 
            className="mobile-menu-button"
            onClick={toggleMenu}
            aria-label="Toggle menu"
          >
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>

        {/* Mobile Navigation */}
        {isMenuOpen && (
          <div className="mobile-nav">
            <Link 
              to="/" 
              className={`mobile-nav-link ${isActive('/') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Home
            </Link>
            
            <div className="mobile-dropdown">
              <button 
                className="mobile-nav-link mobile-dropdown-toggle"
                onClick={() => toggleDropdown('mobile-festival')}
              >
                The Festival
                <ChevronDown size={16} />
              </button>
              {isDropdownOpen === 'mobile-festival' && (
                <div className="mobile-dropdown-menu">
                  <Link 
                    to="/festival" 
                    className="mobile-dropdown-item"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Festival Overview
                  </Link>
                  <Link 
                    to="/festival#highlights" 
                    className="mobile-dropdown-item"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Highlights
                  </Link>
                  <Link 
                    to="/festival#schedule" 
                    className="mobile-dropdown-item"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Schedule
                  </Link>
                </div>
              )}
            </div>

            <Link 
              to="/artists" 
              className={`mobile-nav-link ${isActive('/artists') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Artists
            </Link>

            <div className="mobile-dropdown">
              <button 
                className="mobile-nav-link mobile-dropdown-toggle"
                onClick={() => toggleDropdown('mobile-notion-cup')}
              >
                Notion Cup
                <ChevronDown size={16} />
              </button>
              {isDropdownOpen === 'mobile-notion-cup' && (
                <div className="mobile-dropdown-menu">
                  <Link 
                    to="/notion-cup" 
                    className="mobile-dropdown-item"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Competition
                  </Link>
                  <Link 
                    to="/notion-cup#rules" 
                    className="mobile-dropdown-item"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Rules
                  </Link>
                  <Link 
                    to="/notion-cup#prizes" 
                    className="mobile-dropdown-item"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Prizes
                  </Link>
                </div>
              )}
            </div>

            <Link 
              to="/about" 
              className={`mobile-nav-link ${isActive('/about') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              About
            </Link>

            <Link 
              to="/contact" 
              className={`mobile-nav-link ${isActive('/contact') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Contact
            </Link>

            <Link 
              to="/tickets" 
              className="btn btn-primary mobile-cta"
              onClick={() => setIsMenuOpen(false)}
            >
              Buy Tickets
            </Link>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
