import React from 'react';
import { motion } from 'framer-motion';
import logoImg from '../assets/logo.avif'; // Adjust path if needed
import './About.css';

const About: React.FC = () => {
  // ...values and team arrays remain unchanged...

  return (
    <div className="about-page">
      {/* Hero Section with background image & logo */}
      <section className="about-hero-bg">
        <div className="about-hero-content">
          <img
            src={logoImg}
            alt="Festival Logo"
            className="about-hero-logo"
          />
        </div>
      </section>

      <div className="container">
        {/* About heading and description below hero image */}
        <motion.section
          className="about-hero-text"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <h1 className="page-title">About Latin Notion</h1>
          <p className="page-subtitle">
            Bringing the world's best bachata artists to Dubai for an unforgettable festival experience
          </p>
        </motion.section>
        {/* All your other sections remain unchanged */}
      </div>
    </div>
  );
};

export default About;
