import React from 'react';
import { motion } from 'framer-motion';
import logoImg from '../assets/logo.avif';
import './About.css';
import jayImg from '../assets/jay.avif';
import jay2Img from '../assets/jay2.avif';
import jay3Img from '../assets/jay3.avif';

const About: React.FC = () => {
  const values = [
    {
      icon: <span>‚ù§Ô∏è</span>,
      title: 'Passion for Dance',
      description: 'We believe in the transformative power of bachata to bring people together and create lasting connections.'
    },
    {
      icon: <span>üë•</span>,
      title: 'Community First',
      description: 'Building a global community of dancers who support, inspire, and learn from each other.'
    },
    {
      name: 'JAOYING',
      role: 'ORGANISER - LATIN NOTION THAILAND',
      image: jay3Img// Placeholder for Jaoying's image
    }
  ];

  return (
    <div className="about-page">
      {/* Hero Section with Festival Logo and About Text */}
      <section className="about-hero-section">
        <div className="festival-logo-container">
          <img
            src={logoImg}
            alt="Latin Notion Bachata Festival Logo"
            className="festival-logo"
          />
        </div>
        
        <div className="about-festival-content">
          <h1 className="about-festival-title">ABOUT THE FESTIVAL</h1>
          <div className="about-festival-text">
            <p>
              Latin Notion Bachata Festival an enchanting winter oasis where the sultry rhythms of Caribbean bachata meet Dubai's iconic skyline.. Latin Notion Bachata Festival is a Six-day, season-highlight event, igniting the heart of winter with movement, music, and magic. Founded by dance visionary Jay Bachata, our festival draws passionate bachateros and curious first-timers from around the globe to celebrate connection, creativity, and the joy of dance.
            </p>
          </div>
        </div>
      </section>

      {/* Meet Our Organisers Section */}
      <section className="organizers-section">
        <h2 className="organizers-title">MEET OUR ORGANISERS</h2>
        <div className="organizers-grid">
          {organizers.map((organizer, index) => (
            <motion.div
              key={index}
              className="organizer-card"
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2 }}
            >
              <div className="organizer-image-container">
                <img
                  src={organizer.image}
                  alt={organizer.name}
                  className="organizer-image"
                />
              </div>
              <h3 className="organizer-name">{organizer.name}</h3>
              <p className="organizer-role">{organizer.role}</p>
              {index === 2 && (
                <div className="whatsapp-icon">
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                    <path d="M17.472 14.382c-.297-.149-1.758-.867-2.03-.967-.273-.099-.471-.148-.67.15-.197.297-.767.966-.94 1.164-.173.199-.347.223-.644.075-.297-.15-1.255-.463-2.39-1.475-.883-.788-1.48-1.761-1.653-2.059-.173-.297-.018-.458.13-.606.134-.133.298-.347.446-.52.149-.174.198-.298.298-.497.099-.198.05-.371-.025-.52-.075-.149-.669-1.612-.916-2.207-.242-.579-.487-.5-.669-.51-.173-.008-.371-.01-.57-.01-.198 0-.52.074-.792.372-.272.297-1.04 1.016-1.04 2.479 0 1.462 1.065 2.875 1.213 3.074.149.198 2.096 3.2 5.077 4.487.709.306 1.262.489 1.694.625.712.227 1.36.195 1.871.118.571-.085 1.758-.719 2.006-1.413.248-.694.248-1.289.173-1.413-.074-.124-.272-.198-.57-.347m-5.421 7.403h-.004a9.87 9.87 0 01-5.031-1.378l-.361-.214-3.741.982.998-3.648-.235-.374a9.86 9.86 0 01-1.51-5.26c.001-5.45 4.436-9.884 9.888-9.884 2.64 0 5.122 1.03 6.988 2.898a9.825 9.825 0 012.893 6.994c-.003 5.45-4.437 9.884-9.885 9.884m8.413-18.297A11.815 11.815 0 0012.05 0C5.495 0 .16 5.335.157 11.892c0 2.096.547 4.142 1.588 5.945L.057 24l6.305-1.654a11.882 11.882 0 005.683 1.448h.005c6.554 0 11.89-5.335 11.893-11.893A11.821 11.821 0 0020.885 3.488" fill="#25D366"/>
                  </svg>
                </div>
              )}
            </motion.div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default About;
