import React from 'react';
import './FestivalMedia.css';

interface MediaItem {
  type: 'image' | 'video';
  src: string;
  alt?: string;
}

const mediaItems: MediaItem[] = [
  { type: 'image', src: '/images/img1.jpg', alt: 'Festival Party' },
  { type: 'image', src: '/images/img2.jpg', alt: 'Group Photo' },
  { type: 'video', src: '/videos/teaser.mp4', alt: 'Festival Video' }, // MP4 link
  { type: 'image', src: '/images/img3.jpg', alt: 'Evening Dance' },
  { type: 'image', src: '/images/img4.jpg', alt: 'Outdoor Fun' },
];

const FestivalHighlights: React.FC = () => (
  <section className="festival-media">
    <div className="media-grid">
      {mediaItems.map((item, idx) =>
        item.type === 'image' ? (
          <div className="media-cell" key={idx}>
            <img src={item.src} alt={item.alt} className="media-img" />
          </div>
        ) : (
          <div className="media-cell" key={idx}>
            <video
              src={item.src}
              controls
              autoPlay
              muted
              loop
              className="media-video"
              poster="/images/video-poster.jpg"
            />
          </div>
        )
      )}
    </div>
  </section>
);

export default FestivalHighlights;
