import React from 'react';
import { motion } from 'framer-motion';
import NotionCupPreview from '../components/NotionCupPreview';
import './Artists.css';

const Artists: React.FC = () => {
  return (
    <div className="artists-page">

      {/* 1. Big Poster */}
      <section className="poster-section">
        <img 
          src="/assets/big-poster.jpg" 
          alt="Festival Poster" 
          className="poster-image"
        />
      </section>

      {/* 2. Four Images in Row */}
      <section className="artists-row">
        <img src="/assets/artist1.jpg" alt="Artist 1" />
        <img src="/assets/artist2.jpg" alt="Artist 2" />
        <img src="/assets/artist3.jpg" alt="Artist 3" />
        <img src="/assets/artist4.jpg" alt="Artist 4" />
      </section>

      {/* 3. Notion Cup Preview */}
      <NotionCupPreview />

      {/* 4. Final CTA */}
      <motion.section
        className="artists-cta"
        initial={{ opacity: 0, y: 30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: true }}
      >
        <div className="cta-content">
          <h2>Ready to Learn from the Best?</h2>
          <p>Join us at the Latin Notion Bachata Festival and be part of this amazing experience.</p>
          <div className="cta-buttons">
            <a href="/tickets" className="btn btn-primary">Get Your Tickets</a>
            <a href="/festival" className="btn btn-secondary">Learn More</a>
          </div>
        </div>
      </motion.section>
    </div>
  );
};

export default Artists;
