import React from 'react';
import { motion } from 'framer-motion';
import { Users, Music, Camera, Utensils, Award, Globe, Calendar, MapPin } from 'lucide-react';
import './FestivalHighlights.css';

interface Highlight {
  id: number;
  icon: React.ReactNode;
  title: string;
  description: string;
  features: string[];
}

const highlights: Highlight[] = [
  {
    id: 1,
    icon: <Users size={40} />,
    title: "World-Class International Artists",
    description: "Learn from the best bachata instructors and performers from across the globe.",
    features: [
      "Masterclasses with top-tier instructors",
      "Intensive workshops and training sessions",
      "One-on-one coaching opportunities",
      "Live performances and showcases"
    ]
  },
  {
    id: 2,
    icon: <Music size={40} />,
    title: "Themed Celebration Parties",
    description: "Experience unforgettable nightly events with unique themes and atmospheres.",
    features: [
      "Neon-lit pool parties",
      "Masquerade gala balls",
      "Rooftop vibes under the stars",
      "Intimate desert-lantern socials"
    ]
  },
  {
    id: 3,
    icon: <Award size={40} />,
    title: "Immersive Workshops & Panels",
    description: "Daily deep-dives on musicality, partner connection, styling, and performance.",
    features: [
      "Choreography labs and technique sessions",
      "DJ-curated social dance sessions",
      "Musicality and rhythm workshops",
      "Performance and stage presence training"
    ]
  },
  {
    id: 4,
    icon: <Utensils size={40} />,
    title: "Cultural Fusion Experiences",
    description: "Discover the perfect blend of Latin and Middle Eastern cultures.",
    features: [
      "Latin-Middle Eastern culinary pop-ups",
      "Artisan markets and local crafts",
      "Live percussion showcases",
      "Cultural exchange activities"
    ]
  },
  {
    id: 5,
    icon: <Camera size={40} />,
    title: "Iconic Winter Backdrops",
    description: "Dance in stunning locations that provide the perfect setting for memories.",
    features: [
      "Dramatic desert sunset sessions",
      "Grand pillar-free ballrooms",
      "Rooftop terrace photo moments",
      "Luxury resort venues"
    ]
  }
];

const FestivalHighlights: React.FC = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut",
      },
    },
  };

  return (
    <section className="festival-highlights">
      <div className="container">
        <motion.div
          className="highlights-header"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          <h2 className="section-title">FESTIVAL HIGHLIGHTS</h2>
          <p className="section-subtitle">
            Why you can't miss Latin Notion Bachata Festival?
          </p>
        </motion.div>

        <motion.div
          className="highlights-grid"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          {highlights.map((highlight) => (
            <motion.div
              key={highlight.id}
              className="highlight-card"
              variants={itemVariants}
              whileHover={{ y: -5 }}
              transition={{ duration: 0.3 }}
            >
              <div className="highlight-icon">
                {highlight.icon}
              </div>
              
              <div className="highlight-content">
                <h3 className="highlight-title">{highlight.title}</h3>
                <p className="highlight-description">{highlight.description}</p>
                
                <ul className="highlight-features">
                  {highlight.features.map((feature, index) => (
                    <li key={index} className="feature-item">
                      <span className="feature-bullet">â€¢</span>
                      <span className="feature-text">{feature}</span>
                    </li>
                  ))}
                </ul>
              </div>
            </motion.div>
          ))}
        </motion.div>

        <motion.div
          className="highlights-stats"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.4 }}
          viewport={{ once: true }}
        >
          <div className="stats-grid">
            <div className="stat-item">
              <div className="stat-icon">
                <Calendar size={32} />
              </div>
              <div className="stat-content">
                <h4>6 Days</h4>
                <p>of non-stop bachata</p>
              </div>
            </div>
            
            <div className="stat-item">
              <div className="stat-icon">
                <Users size={32} />
              </div>
              <div className="stat-content">
                <h4>500+</h4>
                <p>dancers from around the world</p>
              </div>
            </div>
            
            <div className="stat-item">
              <div className="stat-icon">
                <Globe size={32} />
              </div>
              <div className="stat-content">
                <h4>20+</h4>
                <p>countries represented</p>
              </div>
            </div>
            
            <div className="stat-item">
              <div className="stat-icon">
                <MapPin size={32} />
              </div>
              <div className="stat-content">
                <h4>Dubai</h4>
                <p>iconic winter location</p>
              </div>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default FestivalHighlights;
