import React from 'react';
import logoImg from '../assets/logo.avif'; // Adjust this path if needed
import './About.css';

const About: React.FC = () => (
  <div className="about-page">
    <section className="about-hero-bg">
      <div className="about-hero-overlay">
        <img
          src={logoImg}
          alt="Festival Logo"
          className="about-hero-logo"
        />
        {/* About Festival Content */}
        <div className="about-festival-text">
          <h1 className="about-title">ABOUT THE FESTIVAL</h1>
          <p className="about-description">
            <strong>Latin Notion Bachata Festival</strong> an enchanting winter oasis where the sultry rhythms of Caribbean bachata meet Dubai’s iconic skyline.<br/><br/>
            <strong>Latin Notion Bachata Festival</strong> is a Six-day, season-highlight event, igniting the heart of winter with movement, music, and magic. Founded by dance visionary Jay Bachata, our festival draws passionate bachateros and curious first-timers from around the globe to celebrate connection, creativity, and the joy of dance.
          </p>
        </div>
      </div>
    </section>
  </div>
);

export default About;
